mapper.select:
select * from	(select rownum rnum, temp.* from (select * from
(select f.*, m.birth, h.visit from friends f, homepage h, member m where f.friend = h.id and m.id = f.id and f.id = #{id})
order by friend asc) temp ) temp2 where temp2.rnum >=#{start} and temp2.rnum <=#{end}

select * from	(select rownum rnum, temp.* from (select * from
(select f.*, m.birth, h.visit from friends f, homepage h, member m where f.friend = h.id and m.id = f.id and f.id = #{id})
order by distance asc) temp ) temp2 where temp2.rnum >=#{start} and temp2.rnum <=#{end}

select * from	(select rownum rnum, temp.* from (select * from
(select f.*, m.birth, h.visit from friends f, homepage h, member m where f.friend = h.id and m.id = f.id and f.id = #{id})
order by visit desc) temp ) temp2 where temp2.rnum >=#{start} and temp2.rnum <=#{end}
